      if (error) return reject(Error('Cannot load the database'));
      var lines = data.split('\n').slice(1, -1);
      var header = data.split('\n').slice(0, 1)[0].split(',');
      var firstnameIndex = header.findIndex(function (ele) {
        return ele === 'firstname';
      });
      var fieldIndex = header.findIndex(function (ele) {
        return ele === 'field';
      });
      var fields = {};
      var students = {};
      var all = {};
      lines.forEach(function (line) {
        var list = line.split(',');
        if (!fields[list[fieldIndex]]) fields[list[fieldIndex]] = 0;
        fields[list[fieldIndex]] += 1;
        if (!students[list[fieldIndex]]) students[list[fieldIndex]] = '';
        students[list[fieldIndex]] += students[list[fieldIndex]] ? ", ".concat(list[firstnameIndex]) : list[firstnameIndex];
      });
      for (var key in fields) {
        if (Object.hasOwnProperty.call(fields, key)) {
          var number = fields[key];
          all[key] = {
            students: "List: ".concat(students[key]),
            number: number
