  var requiredVars = ['DB_HOST', 'DB_PORT', 'DB_DATABASE', 'PORT'];
  requiredVars.forEach(function (key) {
    if (!process.env[key]) {
      console.error("Missing required environment variable: ".concat(key)); // Use backticks here
      process.exit(1);
    }
  });
}
module.exports = envLoader;
