sourcesContent:
const dotenv = require('dotenv');

dotenv.config();

function envLoader() {
  const requiredVars = ['DB_HOST', 'DB_PORT', 'DB_DATABASE', 'PORT'];
  requiredVars.forEach((key) => {
    if (!process.env[key]) {
      console.error(`Missing required environment variable: ${key}`); // Use backticks here
      process.exit(1);
    }
  });
}

module.exports = envLoader;

